import React , { useState } from 'react';
import './App.css';
import Preview from './components/Preview';
import Massage from './components/massage';
import NotesContainer from './components/Notes/NotesContainer';
import NotesList from './components/Notes/NotesList';
import Note from './components/Notes/Note';

function App() {

  const [notes, setNotes] = useState ([]);
  const [title, setTitle] = useState ('');
  const [content, setContent] = useState ('');
  const [selectedNote, setSelectedNote] = useState (null);
  const [creating, setCreating] = useState (false);
  const [editing, setEditing] = useState (false);

  // change the note title
  const changeTitleHandler = (event)=> {
    setTitle(event.target.value)
  }

  // change the note content
  const changeCnotentHandler = (event) => {
    setContent(event.target.value)
  }

  // save the note handler
  const saveNoteHandler = () => {
    const note = {
      id: new Date(),
      title: title,
      content: content
    }

    const uptededNotes = [...notes,note];

    setNotes (uptededNotes);
    setCreating (false);
    setSelectedNote (note.id);
    setTitle ('');
    setContent ('');
  }

  //clicking a note
  const selectNoteHandler = noteId => {
    setSelectedNote(noteId);
  }

  const getAddNote = () => {

    return (
      <div>
        <h2> Voeg maar een nieuw opmerking toe</h2>
        <div>
          <input
            type="text"
            name="title"
            className="form-input mb-30"
            placeholder="Title"
            value={title}
            onChange={changeTitleHandler}
          />

          <textarea
            rows="10"
            name="content"
            className="form-input"
            placeholder="Inhoud"
            value={content}
            onChange={changeCnotentHandler}
          />

          <a href="#" className="button green" onClick={saveNoteHandler}>
            Opslaan
          </a>
        </div>
      </div>
    );
  };

  const getPreview = () => {

    if (notes.length ===0 ) {
      return <Massage title='Er is geen '/>
 }

    if (!selectedNote) {
      return <Massage title='kies maar een opmerking, alstublieft'/>
 }
    const note = notes.find( note => note.id === selectedNote );

    return (
      <div>
        <div className="note-operations">
          <a href="#" >
            <i className="fa fa-pencil-alt" />
          </a>
          <a href="#">
            <i className="fa fa-trash" />  
          </a>
        </div>
        <div>
          <h2>{note.title}</h2>
          <p>{note.content}</p>
        </div>
      </div>
    );
  };

  const addNoteHandler = () =>{
          setCreating(true);
  }
  return (
    <div className="App">
      <NotesContainer >
        <NotesList >
         {note => <Note 
         key={note.id}
         title={note.title} 
         noteClicked={()=>selectNoteHandler(note.id)}
         active = {selectedNote === note.id}/>
         }
        </NotesList>
        <button className="add-btn" onClick={addNoteHandler}>+</button>
      </NotesContainer>
      <Preview>
         {creating ? getAddNote() : getPreview()} 
      </Preview> 
    </div>
  );
}

export default App;
