import React , { useState } from 'react';
import './App.css';
import Preview from './components/Preview';
import Massage from './components/massage';
import NotesContainer from './components/Notes/NotesContainer';
import NotesList from './components/Notes/NotesList';
import Note from './components/Notes/Note';

function App() {

  const [notes, setNotes] = useState ([]);
  const [title, setTitle] = useState ('');
  const [content, setContent] = useState ('');
  const [selectedNote, setSelectedNote] = useState (null);
  const [creating, setCreating] = useState (false);
  const [editing, setEditing] = useState (false);

  //change the title handler
  const changeTitleHandler = (event) => {
    setTitle(event.target.value)
  }


  // change the note content
  const changeCnotentHandler = (event) => {
    setContent(event.target.value)
  }

  // save the note handler
  const saveNoteHandler = () => {
    const note = {
      id: new Date(),
      title: title,
      content: content
    }

    const uptededNotes = [...notes,note];

    setNotes (uptededNotes);
    setCreating (false);
    setSelectedNote (note.id);
    setTitle ('');
    setContent ('');
  }

  //clicking a note
  const selectNoteHandler = noteId => {
    setSelectedNote(noteId);
  }

  // moving to edit a note
  const editingNoteHandler = () => {
     const note=notes.find(note=>note.id===selectedNote)
     setEditing(true);
     setTitle(note.title);
     setContent(note.content);
  }

  //editing the note
  const updateNoteHandler = () => {
    const updateNotes = [...notes];    
    const noteIndex = notes.findIndex(note => note.id === selectedNote);
    updateNotes[noteIndex] = {
      id: selectedNote,
      title: title,
      content: content
    };

    setNotes(updateNotes);
    setEditing(false);
    setTitle('');
    setContent()
  } 
  const getAddNote = () => {

    return (
      <div>
        <h2> Voeg maar een nieuwe opmerking toe</h2>
        <div>
          <input
            type="text"
            name="title"
            className="form-input mb-30"
            placeholder="Title"
            value={title}
            onChange={changeTitleHandler}
          />

          <textarea
            rows="10"
            name="content"
            className="form-input"
            placeholder="Inhoud"
            value={content}
            onChange={changeCnotentHandler}
          />

          <a href="#" className="button green" onClick={saveNoteHandler}>
            Opslaan
          </a>
        </div>
      </div>
    );
  };

  const getPreview = () => {

    if (notes.length ===0 ) {
      return <Massage title='Er is geen notitie'/>
 }

    if (!selectedNote) {
      return <Massage title='kies maar een notitie, alstublieft'/>
 }
    const note = notes.find( note => note.id === selectedNote );

    let noteDisplay = (
      <div>
        <h2>{note.title}</h2>
        <p>{note.content}</p>
      </div>
    )
    if (editing) {
      noteDisplay = (
        <div>
         <h2>Bijwerking een notitie</h2>
          <form>
           <input 
            type="text"
            name="title"
            className="form-input mb-30"
            placeholder="Title"
            value={title}
            onChange={changeTitleHandler}
          />

          <textarea
            rows="10"
            name="content"
            className="form-input"
            placeholder="Inhoud"
            value={content}
            onChange={changeCnotentHandler}
          />
          <a href="#" className="button green" onClick={updateNoteHandler}>

          </a>
          </form>
        </div>
      )
    }

    return (
      <div>
        {!editing && 
        <div className="note-operations">
          <a href="#" onClick={editingNoteHandler} >
            <i className="fa fa-pencil-alt" />
          </a>
          <a href="#">
            <i className="fa fa-trash" />  
          </a>
        </div>
         }

        {noteDisplay}
      </div>
    );
  };

  const addNoteHandler = () =>{
          setCreating(true)
  }
  return (
    <div className="App">
      <NotesContainer >
        <NotesList >
         {notes.map(note => <Note 
         key={note.id}
         title={note.title} 
         noteClicked={()=>selectNoteHandler(note.id)}
         active = {selectedNote === note.id}/>)
         }
        </NotesList>
        <button className="add-btn" onClick={addNoteHandler}>+</button>
      </NotesContainer>
      <Preview>
         {creating ? getAddNote() : getPreview()} 
      </Preview> 
    </div>
  );
}

export default App;
